import { Container, Row, Col } from "react-bootstrap";
import AppNavbar from "./componets/Navbar";
import Filters from "./componets/Filters";
import CourseCard from "./componets/CourseCard";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const courses = [
  {
    badge: "Forums",
    title: "Co-curricular and Professional Development",
    image: "https://via.placeholder.com/300x150",
    credits: 2,
    description: "Covers soft skills and professional development workshops.",
  },
  {
    badge: "ETEC - Engineering Technology",
    title: "ENPR 44052 - Lean/Six Sigma Management",
    image: "https://via.placeholder.com/300x150",
    credits: 3,
    description: "Focuses on Lean and Six Sigma principles in engineering projects.",
  },
  {
    badge: "ETEC - Engineering Technology",
    title: "ETEC 22033 - Applied Thermodynamics (2020/21)",
    image: "https://via.placeholder.com/300x150",
    credits: 4,
    description: "Explores thermodynamics concepts and industrial applications.",
  },
  {
    badge: "ETEC - Engineering Technology",
    title: "ETEC 43018 - Capstone Project (2022/23)",
    image: "https://via.placeholder.com/300x150",
    credits: 6,
    description: "Final year project focusing on research and innovation.",
  },
];

  return (
    <>
      <AppNavbar />
      <Container className="my-4">
        <h2 className="mb-3">Course Overview</h2>
        <Filters />
        <Row className="g-4">
          {courses.map((course, idx) => (
            <Col md={4} sm={6} key={idx}>
              <CourseCard {...course} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}

export default App;
