import { Card, Dropdown } from "react-bootstrap";
import { ThreeDotsVertical } from "react-bootstrap-icons";

export default function CourseCard({ badge, title, image }) {
  return (
    <Card className="shadow-sm h-100">
      <div className="position-relative">
        <Card.Img variant="top" src={image} style={{ height: "150px", objectFit: "cover" }} />
        <span className="badge bg-primary position-absolute top-0 start-0 m-2">
          {badge}
        </span>
        <Dropdown className="position-absolute bottom-0 end-0 m-2">
          <Dropdown.Toggle variant="light" size="sm">
            <ThreeDotsVertical />
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item>View</Dropdown.Item>
            <Dropdown.Item>Unenroll</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </div>
      <Card.Body>
        <Card.Title className="fs-6">{title}</Card.Title>
      </Card.Body>
    </Card>
  );
}
