import { Container, Row, Col } from "react-bootstrap";
import AppNavbar from "./Navbar";
import Filters from "./Filters";
import CourseCard from "./CourseCard";

function App() {
  const courses = [
    {
      badge: "Forums",
      title: "Co-curricular and Professional Development",
      image: "https://via.placeholder.com/300x150",
    },
    {
      badge: "ETEC - Engineering Technology",
      title: "ENPR 44052 - Lean/Six Sigma Management",
      image: "https://via.placeholder.com/300x150",
    },
    {
      badge: "ETEC - Engineering Technology",
      title: "ETEC 22033 - Applied Thermodynamics (2020/21)",
      image: "https://via.placeholder.com/300x150",
    },
    {
      badge: "ETEC - Engineering Technology",
      title: "ETEC 43018 - Capstone Project (2022/23)",
      image: "https://via.placeholder.com/300x150",
    },
  ];

  return (
    <>
      <AppNavbar />
      <Container className="my-4">
        <h2 className="mb-3">Course Overview</h2>
        <Filters />
        <Row className="g-4">
          {courses.map((course, idx) => (
            <Col md={4} sm={6} key={idx}>
              <CourseCard {...course} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}

export default App;
