import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Filters from "./componets/Filters";
import CourseCard from "./componets/CourseCard";
import CustomNavbar from "./componets/Navbar"; // rename import
import 'bootstrap/dist/css/bootstrap.min.css';

export default function App() {
  const [view, setView] = useState("card");

  const courses = [
    {
      badge: "Forums",
      title: "Co-curricular and Professional Development",
      image: "https://tse4.mm.bing.net/th/id/OIP.tIfT_NDtlxHdAxdzTbQmPAHaEK?cb=ucfimgc2&rs=1&pid=ImgDetMain&o=7&rm=3",
      credits: 3,
      description: "Learn valuable skills and participate in forums to develop professionally."
    },
    {
      badge: "Workshop",
      title: "Advanced Programming",
      image: "https://www.taylor.edu/_images/academics/degrees/mechanicalengineering-pisr-engineering.webp",
      credits: 4,
      description: "Deep dive into programming languages and hands-on exercises."
    },
    {
      badge: "Seminar",
      title: "Entrepreneurship Basics",
      image: "https://tse2.mm.bing.net/th/id/OIP.jxgVBPtHmgb0DL8YpcR4LgHaEK?cb=ucfimgc2&rs=1&pid=ImgDetMain&o=7&rm=3",
      credits: 2,
      description: "Understand how to start and manage your own business."
    }
  ];

  return (
    <>
      <CustomNavbar /> {/* use custom navbar */}

      <Container className="my-4">
        <Filters view={view} setView={setView} />

        <Row className={view === "card" ? "g-3" : "flex-column"}>
          {courses.map((course, idx) => (
            <Col
              key={idx}
              md={view === "card" ? 4 : 12}
            >
              <CourseCard {...course} view={view} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}
