import { useState, useEffect } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import Filters from "./componets/Filters";
import CourseCard from "./componets/CourseCard";
import CourseForm from "./componets/CourseForm";
import { getCourses, deleteCourse } from "./services/api";
import CustomNavbar from "./componets/Navbar";

export default function App() {
  const [view, setView] = useState("card");
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editCourse, setEditCourse] = useState(null);

  const fetchCourses = async () => {
    const res = await getCourses();
    setCourses(res.data);
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleEdit = (course) => {
    setEditCourse(course);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    await deleteCourse(id);
    fetchCourses();
  };

  return (
    <>
      <CustomNavbar />
      <Container className="my-4">
        <Button className="mb-3" onClick={() => { setEditCourse(null); setShowForm(true); }}>
          Add Course
        </Button>

        <Filters view={view} setView={setView} />

        <Row className={view === "card" ? "g-3" : "flex-column"}>
          {courses.map((course) => (
            <Col key={course.id} md={view === "card" ? 4 : 12}>
              <CourseCard
                {...course}
                course={course}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            </Col>
          ))}
        </Row>

        {showForm && (
          <CourseForm
            show={showForm}
            onClose={() => setShowForm(false)}
            course={editCourse}
            refreshCourses={fetchCourses}
          />
        )}
      </Container>
    </>
  );
}
