import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Filters from "./componets/Filters";
import CourseCard from "./componets/CourseCard";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function App() {
  const [view, setView] = useState("card");

  const courses = [
    {
      badge: "Forums",
      title: "Co-curricular and Professional Development",
      image: "https://via.placeholder.com/300x150",
      credits: 3,
      description: "Learn valuable skills and participate in forums to develop professionally."
    },
    {
      badge: "Workshop",
      title: "Advanced Programming",
      image: "https://via.placeholder.com/300x150",
      credits: 4,
      description: "Deep dive into programming languages and hands-on exercises."
    },
    {
      badge: "Seminar",
      title: "Entrepreneurship Basics",
      image: "https://via.placeholder.com/300x150",
      credits: 2,
      description: "Understand how to start and manage your own business."
    }
  ];

  return (
    <Container className="my-4">
      <Filters view={view} setView={setView} />

      <Row className={view === "card" ? "g-3" : "flex-column"}>
        {courses.map((course, idx) => (
          <Col
            key={idx}
            md={view === "card" ? 4 : 12}
          >
            <CourseCard {...course} view={view} />
          </Col>
        ))}
      </Row>
    </Container>
  );
}
